import React, { useEffect, useState } from "react";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Prize from "./Prize";
import axios from "axios";

export default function PrizeContainer() {
  const [prizes, setPrizes] = useState([
    { title: "null", description: "null", point_value: 0, counter: 0 }
  ]);

  useEffect(() => {
    axios
      .get("http://localhost:5000/prizes")
      .then(res => {
        setPrizes(res.data);
      })
      .catch(err => {
        console.log(err);
      });
  }, []);

  const prizeList = prizes.map((prize,index) => (
      <Col key={index}>
        <Prize
          title={prize.title}
          description={prize.description}
          pointVal={prize.point_value}
          counter={prize.counter}
        />
      </Col>
    ));

  console.log(prizes[0].title);
  return (
    <Container className="prize-container">
      <Row className="prize-row">
          {prizeList.slice(0,3)}
      </Row>
      <Row>
          {prizeList.slice(3,6)}
      </Row>
      <Row>
          {prizeList.slice(6,9)}
      </Row>
      <Row>
          {prizeList.slice(9,13)}
      </Row>
    </Container>
  );
}
