import React, {useState} from "react";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import Alert from "react-bootstrap/Alert";

export default function Prize({ title, pointVal, description, counter }) {
  const [show, setShow] = useState(false);
  if (show) {
    return (
      <Alert variant="danger" onClose={() => setShow(false)} dismissible>
        <Alert.Heading>Oh snap! You got an error!</Alert.Heading>
        <p>
          Change this and that and try again. Duis mollis, est non commodo
          luctus, nisi erat porttitor ligula, eget lacinia odio sem nec elit.
          Cras mattis consectetur purus sit amet fermentum.
        </p>
      </Alert>
    );
  }

  return (
    <Card className="prize">
      <Card.Header style={{ borderBottomColor: "#66cf8c" }}>
        {" "}
        <strong>
          {pointVal} Pts <span style={{ float: "right" }}>{counter} left</span>
        </strong>
      </Card.Header>
      <Card.Body style={{ textAlign: "center" }}>
        <Card.Title>{title}</Card.Title>
        <Card.Text>{description}</Card.Text>
        <Button className="prize-button">Redeem</Button>
      </Card.Body>
    </Card>
  );
}
