import React, { createContext, useReducer, useEffect, useState } from "react";
//useReducer helps us connect the reducer to the state
import AppReducer from "./AppReducer";
import axios from "axios";

export const GlobalContext = createContext();

export const GlobalContextProvider = ({ children }) => {
  const initState = {
    points: 0,
    questionCounter: 0
  };

  const [initialState, setInitialState] = useState(initState)

  useEffect(() => {
    axios
      .get("http://localhost:5000/users")
      .then(res => {
          setInitialState({
            points: res.data[0].points,
            questionCounter: res.data[0].questionCounter
          });
      })
      .catch(err => console.log(err));
  }, []);

  console.log(initialState)
  const [state, dispatch] = useReducer(AppReducer, initialState);
  console.log(state)
  //   useEffect(() => {
  //     localStorage.setItem(
  //       "incomeTransactions",
  //       JSON.stringify(state.incomeTransactions)
  //     );
  //     localStorage.setItem(
  //       "expenseTransactions",
  //       JSON.stringify(state.expenseTransactions)
  //     );
  //   });

  //   const addIncome = incomeTransaction => {
  //     dispatch({
  //       type: "ADD_INCOME",
  //       payload: incomeTransaction
  //     });
  //   };

  return (
    <GlobalContext.Provider
      value={{
        points: state.points,
        questionCounter: state.questionCounter
      }}
    >
      {children}
    </GlobalContext.Provider>
  );
};
