import React, { useState, useContext } from "react";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import Alert from "react-bootstrap/Alert";
import axios from "axios";
import { GlobalContext } from "../context/GlobalState";

export default function Prize({ title, pointVal, description, counter, id }) {
  const [show, setShow] = useState(false);
  const [error, setError] = useState(false);
  const { points } = useContext(GlobalContext);

  const newCount = counter - 1;
  const newPoints = points - pointVal;

  const redeemPrize = e => {
    e.preventDefault();

    const updatePrize = {
      counter: counter - 1
    };

    const updatePoints = {
      points: points - pointVal
    };

    axios
      .post(`http://localhost:5000/prizes/update/${id}`, updatePrize)
      .then(res => console.log("Successfully updated prize"))
      .catch(err => console.log(err));

